import React from 'react';
import Card from './Card';

export default class Deck extends React.Component {
    render() {
        return (
    <div style={container}>
      
        <Card style={square}
        name = "test"
        pv = "1"
        attack = "5"
        img = "plant.png"
        />
        <Card style={square}/>
        <Card style={square}/>
    </div>


            );
    }
}

    const container = {
        position:'absolute',
        bottom:0,
        width:'100%',
        backgroundColor: "#7CA1B4",
        alignItems: "center", // ignore this - we'll come back to it
        justifyContent: "center", // ignore this - we'll come back to it
        flexDirection: "row",
      };

      const square = {
        backgroundColor: "#7cb48f",
        width: 100,
        height: 100,
        margin: 4,
        shadowColor: '#171717',
        shadowOffset: {width: -2, height: 4},
        shadowOpacity: 0.2,
        shadowRadius: 3,
    
      };
